import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import { blue } from "@material-ui/core/colors";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import branches from '../../../../assets/data/branches.json'

const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600],
  },
  dialog: {
    position: "absolute",
    height: '60vh',
    width: '40vw',
    right: 10,
    bottom: 50,
  },
});

const BranchMap = ({ isMapOpen, handleClose }) => {
  const classes = useStyles();

  const [i, setI] = useState(0)
  const [position, setPosition] = useState([-33.958476, 18.460276])
  const [province, setProvince] = useState(branches[i].address.province)
  const [address, setAddress] = useState(branches[i].address.address)
  const [map, setMap] = useState(null)

  const changeLocation = (index) => {
    setI(i+index)
    setProvince(branches[i].address.province)
  }

  return (
    <div>
      <Dialog
        open={isMapOpen}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        classes={{
          paper: classes.dialog,
        }}
      >
        <DialogTitle id="alert-dialog-title">
          {province}
        </DialogTitle>
        <DialogActions>
          <Button onClick={handleClose} color="primary" autoFocus>
            Close
          </Button>
        </DialogActions>
        <DialogContent>
          <MapContainer center={position} zoom={13} scrollWheelZoom={true} whenCreated={map => setMap(map)}>
            <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {branches.map(branch => (
              <Marker
                key={branch.id}
                position={[branch.gps.latitude,  branch.gps.longitude]}
              >
                <Popup>
                  A pretty CSS3 popup. <br /> Easily customizable.
                </Popup>
              </Marker>
            ))}
            </MapContainer>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => changeLocation(-1)} color="primary" autoFocus disabled={i === 0}>
            Previous
          </Button>
          <Button onClick={() => changeLocation(1)} color="primary" autoFocus disabled={i === 8}>
            Next
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default BranchMap;
