import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Grid,  Typography } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    root: {
        // flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: "center",
        color: theme.palette.text.secondary,
    },
}));

const Home = () => {
    function getWindowDimensions() {
        const { innerWidth: width, innerHeight: height } = window;
        return {
            width,
            height,
        };
    }

    function useWindowDimensions() {
        const [windowDimensions, setWindowDimensions] = useState(
            getWindowDimensions()
        );

        useEffect(() => {
            function handleResize() {
                setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener("resize", handleResize);
            return () => window.removeEventListener("resize", handleResize);
        }, []);

        return windowDimensions;
    }

    const { height, width } = useWindowDimensions();

    const classes = useStyles();

    return (
        <div style={{height: height - 105 }}>
            <Grid container style={{height:'100%'}} >
                <Grid item xs={5}>
                    <Grid container direction="column" style={{backgroundColor: '#00ff00', height:'100%', alignItems: 'center'}} >
                        <Typography variant='h2' style={{width:'75%', }}>
                            Heading
                        </Typography>
                        <Typography variant='body1' style={{width:'75%'}}>
                        Green tech refers to a type of technology that is considered environmentally friendly based on its production process.
                        Although the market for green technology is a relatively young, it has garnered a significant amount of investor interest due to increasing awareness about the impacts of climate change and the depletion of natural resources.
                        </Typography>
                        <Typography variant='body1' style={{width:'75%'}}>
                            Here at GreenTech we specialize in fabricating technology for the future out of sustainable materials. From solar panels to reusable rockets, GreenTech is here to empower progress and save the planet.
                        </Typography>
                    </Grid>
                </Grid>
                <Grid item xs={7}>
                    <Grid container direction="column" style={{backgroundColor: '#ffff00', height:'100%', alignItems: 'center'}} >

                    </Grid>
                </Grid>
            </Grid>
        </div>
    );
};

export default Home;
