import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Grid } from "@material-ui/core";
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    heading: {
        fontSize: theme.typography.pxToRem(15),
        fontWeight: theme.typography.fontWeightRegular,
    },
}));

const Activity1 = () => {
    function getWindowDimensions() {
        const { innerWidth: width, innerHeight: height } = window;
        return {
            width,
            height,
        };
    }

    function useWindowDimensions() {
        const [windowDimensions, setWindowDimensions] = useState(
            getWindowDimensions()
        );

        useEffect(() => {
            function handleResize() {
                setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener("resize", handleResize);
            return () => window.removeEventListener("resize", handleResize);
        }, []);

        return windowDimensions;
    }

    const { height, width } = useWindowDimensions();

    const classes = useStyles();

    return (
        <div style={{maxHeight: height - 105, overflow:"auto"}}>
            <Grid direction="column" container justify="center" alignItems="center">
            <Typography variant="h4" gutterBottom style={{marginTop:50, color:'#006e39',}}>
                Renewable Energy
            </Typography>
            <Typography variant="h5" gutterBottom style={{marginTop:50, width: '60vw', textAlign: 'center'}}>
                Here at GreenTech we provide both the technology and support services to set up custom renewable solutions for South African businesses and households. Often incorporating renewable sources into infrustructure can be overwhelming and so GreenTech provides step-by-step installation and on-site support.
            </Typography>
            <Typography variant="h5" gutterBottom style={{marginTop:50, width: '60vw', textAlign: 'center'}}>
                Of all South African renewable energy sources, solar holds the most potential. Because of the country's geographic location, it receives large amounts of solar energy. Wind energy is also a major potential source of renewable energy. Due to the high wind velocity on the coast of the country, Cape Town has implemented multiple wind farms, which generate significant amounts of energy. Renewable energy systems in the long-term are comparable or cost slightly less than non-renewable sources. Biomass is currently the largest renewable energy contributor in South Africa with 9-14% of the total energy mix. Renewable energy systems are costly to implement in the beginning but provide high economic returns in the long-run.
            </Typography>
            <Typography variant="h5" gutterBottom style={{marginTop:10, width: '60vw', textAlign: 'center'}}>
                    <Link href="https://en.wikipedia.org/wiki/Renewable_energy_in_South_Africa" color="inherit">
                        (Wikipedia)
                    </Link>
                </Typography>
            </Grid>
        </div>
    );
}

export default Activity1
