import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Grid,  Typography, Accordion, AccordionSummary, AccordionDetails } from "@material-ui/core";
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    heading: {
        fontSize: theme.typography.pxToRem(15),
        fontWeight: theme.typography.fontWeightRegular,
    },
}));

const Home = () => {
    function getWindowDimensions() {
        const { innerWidth: width, innerHeight: height } = window;
        return {
            width,
            height,
        };
    }

    function useWindowDimensions() {
        const [windowDimensions, setWindowDimensions] = useState(
            getWindowDimensions()
        );

        useEffect(() => {
            function handleResize() {
                setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener("resize", handleResize);
            return () => window.removeEventListener("resize", handleResize);
        }, []);

        return windowDimensions;
    }

    const { height, width } = useWindowDimensions();

    const classes = useStyles();

    return (
        <div style={{height: height - 105 }}>
            <Grid container style={{height:'100%'}} >
                <Grid item xs={5}>
                    <Grid container direction="column" style={{backgroundColor: '#00ff00', height:'100%', alignItems: 'center'}} >
                        <Typography variant='h3' style={{width:'75%', marginTop:'2%', marginBottom:'2%', textAlign: 'center'}}>
                            What is GreenTech?
                        </Typography>
                        <Typography variant='body1' style={{width:'75%', marginTop:'2%', marginBottom:'2%', textAlign: 'center', fontFamily:'Roboto'}}>
                        Green tech refers to a type of technology that is considered environmentally friendly based on its production process.
                        Although the market for green technology is a relatively young, it has garnered a significant amount of investor interest due to increasing awareness about the impacts of climate change and the depletion of natural resources.
                        </Typography>
                        <Typography variant='body1' style={{width:'75%', marginTop:'2%', marginBottom:'2%', textAlign: 'center', fontFamily:'Roboto'}}>
                            Here at GreenTech we specialize in fabricating technology for the future out of sustainable materials. From solar panels to reusable rockets, GreenTech is here to empower progress and save the planet.
                        </Typography>
                        <div className={classes.root}>
                        <Accordion>
                            <AccordionSummary
                            expandIcon={<ExpandMoreIcon />}
                            aria-controls="panel1a-content"
                            id="panel1a-header"
                            >
                            <Typography className={classes.heading}>Accordion 1</Typography>
                            </AccordionSummary>
                            <AccordionDetails>
                            <Typography>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse malesuada lacus ex,
                                sit amet blandit leo lobortis eget.
                            </Typography>
                            </AccordionDetails>
                        </Accordion>
                        <Accordion>
                            <AccordionSummary
                            expandIcon={<ExpandMoreIcon />}
                            aria-controls="panel2a-content"
                            id="panel2a-header"
                            >
                            <Typography className={classes.heading}>Accordion 2</Typography>
                            </AccordionSummary>
                            <AccordionDetails>
                            <Typography>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse malesuada lacus ex,
                                sit amet blandit leo lobortis eget.
                            </Typography>
                            </AccordionDetails>
                        </Accordion>
                        <Accordion disabled>
                            <AccordionSummary
                            expandIcon={<ExpandMoreIcon />}
                            aria-controls="panel3a-content"
                            id="panel3a-header"
                            >
                            <Typography className={classes.heading}>Disabled Accordion</Typography>
                            </AccordionSummary>
                        </Accordion>
                        </div>
                    </Grid>
                </Grid>
                <Grid item xs={7}>
                    <Grid container direction="column" style={{backgroundColor: '#ffff00', height:'100%', alignItems: 'center'}} >

                    </Grid>
                </Grid>
            </Grid>
        </div>
    );
};

export default Home;
