import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Accordion from "@material-ui/core/Accordion";
import { Grid } from "@material-ui/core";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import OrderedList from "./components/OrderedList";
import img1 from '../../../../images/renewable.jpg'
import img2 from '../../../../images/smartfarming.jpg'
import img3 from '../../../../images/aerospace.jpg'


const useStyles = makeStyles((theme) => ({
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  large: {
    width: theme.spacing(10),
    height: theme.spacing(10),
    marginRight: theme.spacing(3),
  },
}));

export default function SimpleAccordion() {
  const classes = useStyles();

  const listInfo = [
    {
      key: 1,
      primary: "Renewable Energy",
      secondary: "Disrupt the fossil fuel industry",
      src: img1,
      products: ["Solar panels", "Biofuel technology", "Wind turbine"]
    },
    {
      key: 2,
      primary: "Smart farming",
      secondary: "Grow intelligently to provide for everyone",
      src: img2,
      products: ["Aquaponics", "Autonomous farming equipment", "Drone surveys"]
    },
    {
      key: 3,
      primary: "Aerospace Manufacturing",
      secondary: "Travel faster, further and cleaner",
      src: img3,        
      products: ["Reusable rockets", "Remotely sensed satellites", "Rovers"]
    },
  ];

  function insideAvatarFunction(e) {
    e.stopPropagation();
    console.log('Fired insideAvatarFunction()!');
  }

  return (
    <Grid
      container
      direction="column"
      style={{
        alignItems: "center",
        marginTop: "5%",
        marginBottom: "5%",
      }}
    >
      <ul style={{ width: "100%" }}>
        {listInfo.map((info) => (
          <li>
            <Accordion
              key={info.key}
              style={{ width: "90%", marginBottom: "3%", borderRadius: 10 }}
            >
              <AccordionSummary
                expandIcon={<ExpandMoreIcon />}
                aria-controls="panel1a-content"
                id="panel1a-header"
              >
                <ListItem>
                  <ListItemAvatar>
                    <div onClick={insideAvatarFunction}>
                    <Avatar alt="Remy Sharp" src={info.src} className={classes.large} />

                    </div>
                  </ListItemAvatar>
                  <ListItemText
                    primary={info.primary}
                    secondary={info.secondary}
                  />
                </ListItem>
              </AccordionSummary>
              <AccordionDetails>
                <OrderedList Products={info.products}/>
              </AccordionDetails>
            </Accordion>
          </li>
        ))}
      </ul>
    </Grid>
  );
}
